Item.method_code:
var inn =top.aras.newIOMInnovator();
var ItemId = this.getID();
var buildHTMLStart ="";

// Calling Procedure //

var proc = this.newItem("SQL", "SQL PROCESS");
proc.setProperty("name", "cust_see_methods");
proc.setProperty("PROCESS", "CALL");
proc.setProperty("ARG1",ItemId);
var result = proc.apply();



if(result.getItemCount()>0)

  {   
        buildHTMLStart +="<html>";
		buildHTMLStart +="<head>";
		buildHTMLStart +="</head>";
		buildHTMLStart += "<body>";
	    buildHTMLStart += "<table border= '1px solid black' width = '100%'>";
		buildHTMLStart += "<tr>";
		buildHTMLStart += "<th align='center'>Object Type</th>";
		buildHTMLStart += "<th align='center'>Object Name</th> ";
		buildHTMLStart += "<th align='center'>Package Name</th> ";

		buildHTMLStart += "</tr>";



        for(var i=0;i<result.getItemCount();i++)

            {

            var obj= result.getItemByIndex(i).getProperty("methodtype","");
            var obj1=result.getItemByIndex(i).getProperty("methodname", "");
			var packageDetail=result.getItemByIndex(i).getProperty("packagename", "");




            buildHTMLStart += "<tr>";
          	buildHTMLStart += "<td>"+ obj +"</td>";
          	buildHTMLStart += "<td>"+ obj1 +"</td>";
          	buildHTMLStart += "<td>"+ packageDetail +"</td>";

            buildHTMLStart += "</tr>";                                    

            }

        buildHTMLStart +="</table>";
        buildHTMLStart += "</body>";

        buildHTMLStart +="</html>";
    }

 return buildHTMLStart;
